import React, {useEffect, useState} from 'react';
import "./Home.css";
import SearchBar from './SearchBar';
import RestaurantsList from './RestaurantsList';

const Home = () => {
  const [restaurants, setRestaurants] = useState([])
  const [search, setSearch] = useState({
    result: []
  })

    useEffect(() => {
        fetch("http://localhost:3000/restaurants")
        .then(response => response.json())
        .then(restaurants => setRestaurants(restaurants))
    }, [])

    function filterSearch(input){
        restaurants.filter((restaurant) => {
          console.log(restaurant)
          if(restaurant.neighborhood.toLowerCase().includes(input.toLowerCase())) return true;
          return (input === restaurant.neighborhood) || (input === restaurant.cuisine_type)
        })
        setSearch((prev) => {

          return {...prev, result:search.result}
        })
    }

  return (
    <div className="App">
      <SearchBar onSearch={filterSearch} placeholder="Enter a Book Name..." />
      <RestaurantsList result={restaurants}/>
    </div>
  )
}

export default Home;
