import React, {useEffect, useState} from 'react';
import "./Home.css";
import SearchBar from './SearchBar';
import RestaurantsList from './RestaurantsList';

const Home = () => {
  const [restaurants, setRestaurants] = useState([])
  const [search, setSearch] = useState([])

    useEffect(() => {
        fetch("http://localhost:3000/restaurants")
        .then(response => response.json())
        .then(restaurants => setRestaurants(restaurants))
    }, [])

    function filterSearch(input){
        restaurants.filter((restaurant) => {
          return (input.toLowerCase() === restaurant.neighborhood.toLowerCase()) || (input.toLowerCase() ===restaurant.cuisine_type.toLowerCase())
        })
        setSearch((prev) => {
          return [...prev, search]
          console.log(search)
        })
    }

  return (
    <div className="App">
      <SearchBar onSearch={filterSearch} placeholder="Enter a Book Name..." />
      <RestaurantsList result={search}/>
    </div>
  )
}

export default Home;
